import logo from './logo.svg';
import './App.css';
function Hearder(props){
  return <header>
          <h1><a href="/" onClick={(e)=>{
            e.preventDefault();
            props.onChangeMode();
          }}>{props.title}</a></h1>
        </header>
}
function Nav(props){
  const lis = []
  for(let i =0; i < props.topics.length; i++){
    let t = props.topics[i];
    lis.push(<li key={t.id}>
      <a id={t.id}href={'/read/'+t.id} onClick={(e)=>{
        e.preventDefault();
        props.onChangeMode(e.target.id);
      }}
      >{t.title}</a></li>)
  }
  return <nav>
  <ol>
    {lis}
  </ol>
</nav>
}
function Article(props){
  return <article>
  <h2>{props.title}</h2>
  {props.body}
</article>
}
function App() {
  const mode = 'WELCOME';
  const topics = [
    {id:1, title:'HTML', body:'html is ...' },
    {id:2, title:'CSS', body:'css is ...' },
    {id:3, title:'JS', body:'js is ...' },
  ]
  let content = null;
  if(mode === 'WELCOME'){
    content = <Article title="WELCOME" body="Hello, React"></Article>
  } else if(mode === 'READ'){
    content = <Article title="Read" body="Hello, Read"></Article>
  }
  return (
    <div>
      <Hearder title="REACT" onChangeMode={()=>{
        alert('Header');
      }}></Hearder>
      <Nav topics={topics} onChangeMode={(id)=>{
        alert(id);
      }}></Nav>
      {content}
    </div>
  );
}

export default App;
